                                <Link to={`/ProductDetail/${item.id}`}>
                                  <strong>{item.TenSanPham}</strong>
                                </Link>
                              </th>
                              <th
                                className="qty"
                                style={{
                                  display: "flex",
                                  marginTop: "30px",
                                  justifyContent: "space-evenly",
                                }}
                              >
                               
                                <button
                                  onClick={() => onRemove(item)}
                                  className="remove"
                                >
                                  -
                                </button>
                                {/* <div className="cart-qty"> 
                                
                                </div> */}
                                  <Form>
                                      <Form.Control type="text" value={item.qty}/>
                                  </Form>
                                <button
                                  onClick={() => onAdd(item)}
                                  className="add"
                                >
                                  +
                                </button>
                              </th>
                              <th className="price">
                                <NumberFormat
                                  value={item.GiaKM}
