                          <div className="title_box_cart">
                            1. Thông tin khách hàng
                          </div>
                          <div className="box-cart-user-info">
                            <input
                              name="name"
                              placeholder="Họ tên"
                              value={name}
                              onChange={(e) => setName(e.target.value)}
                            />
                            <input
                              name="name"
                              placeholder="Email"
                              value={userLogin.Email}
                              disabled
                            />
                            <input
                              name="name"
                              placeholder="Số điện thoại"
                              value={phone}
                              onChange={(e) => setPhone(e.target.value)}
                              required
                            />
                            <input
                              name="name"
                              placeholder="Địa chỉ"
                              value={address}
                              onChange={(e) => setAddress(e.target.value)}
                              required
                            />
                          </div>
                          <div className="box-cart-user-info">
                            <div className="title_box_cart">
                              2. Hình thức thanh toán
                            </div>
                            <div
                              className="left"
                              style={{ marginRight: "15px" }}
                            >
                              <input
                                name="Cash"
                                type="radio"
                                name="payment"
                                value="1"
                                onChange={(e) =>
                                  setOptionPayment(e.target.value)
                                }
                              />
                              <label
                                style={{
                                  display: "inline-block",
                                  lineHeight: "20px",
                                  marginLeft: "15px",
                                }}
                              >
                                Tiền mặt
                              </label>
                            </div>

                            <div id="momo">
                              <input
                                name="payment_momo"
                                type="radio"
                                name="payment"
                                value="2"
                                onChange={(e) =>
                                  setOptionPayment(e.target.value)
                                }
                              />
                              <label
                                style={{
                                  display: "inline-block",
                                  lineHeight: "20px",
                                  marginLeft: "15px",
                                }}
                              >
                                <i className="cps-zalopay"></i>
                                <span> Cổng thanh toán MOMO</span>
                              </label>
                            </div>
                            <div id="paypal">
                              <input
                                name="payment_paypal"
                                type="radio"
                                name="payment"
                                value="3"
                                onChange={(e) =>
                                  setOptionPayment(e.target.value)
                                }
                              />
                              <label
                                style={{
                                  display: "inline-block",
                                  lineHeight: "20px",
                                  marginLeft: "15px",
                                }}
                              >
                                <i className="fab fa-cc-paypal"></i>
                                <span> Paypal</span>
                              </label>
                            </div>
                            <div id="vnpay">
                              <input
                                name="payment_vnpay"
                                type="radio"
                                name="payment"
                                value="4"
                                onChange={(e) =>
                                  setOptionPayment(e.target.value)
                                }
                              />
                              <label
                                style={{
                                  display: "inline-block",
                                  lineHeight: "20px",
                                  marginLeft: "15px",
                                }}
                              >
                                <i className="cs-vnpay"></i>
                                <span> Cổng thanh toán VNPAY</span>
                              </label>
                            </div>
                          </div>
                          <div className="box-cart-user-info">
                            <div className="title_box_cart">
                              3. Hình thức giao hàng
                            </div>
                            <div
                              className="left"
                              style={{ marginRight: "15px" }}
                            >
                              <input
                                name="Shipping"
                                type="radio"
                                value="1"
                                onChange={(e) =>
                                  setOptionShipping(e.target.value)
                                }
                                checked
                              />
                              <label
                                style={{
                                  display: "inline-block",
                                  lineHeight: "20px",
                                  marginLeft: "15px",
                                }}
                              >
                                COD
                              </label>
                            </div>
                          </div>
                          {paypal ? (
                            <Paypal
                              Moneyconversion={Moneyconversion(totalPrice)}
                            />
                          ) : (
                            ""
                          )}
                        </div>
                      </div>
                      <div className="col-xl-12 col-md-12 cart-buttons inner-right inner-left">
                        <div className="buttons">
                          <Button
                            id="checkout"
                            name="checkout"
                            type="submit"
                            onClick={handleOrder}
                          >
                            Thanh toán
                          </Button>
                        </div>
                        <Backdrop
                          open={openBackDrop}
                          className="backdrop-mui"
                          onClick={handleCloseBackdrop}
                        >
                          <CircularProgress color="inherit" />
                        </Backdrop>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default Cart;
