import React, { useState } from "react";
import Header from "../../../Component/Header/Header";
import Footer from "../../../Component/Footer/Footer";
import { Button, FormControl } from "react-bootstrap";
import TextField from "@material-ui/core/TextField";
import { useParams } from "react-router";
import Alert from "@material-ui/lab/Alert";
import axios from "axios";
import Snackbar from "@material-ui/core/Snackbar";
import MuiAlert from "@material-ui/lab/Alert";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";
import "../Css/Account.css";
import { useForm } from "react-hook-form";
import {
  IconButton,
  InputAdornment,
  InputLabel,
  OutlinedInput,
} from "@material-ui/core";
import { Visibility, VisibilityOff } from "@material-ui/icons";
function ResetPassword() {
  const [password, setPassword] = useState("");
  const [password_confirm, setPassword_confirm] = useState("");
  const [error, setError] = useState("");
  const [open, setOpen] = React.useState(false);
  const [redirect, setRedirect] = useState(false);
  let { token } = useParams();
  const [values, setValues] = React.useState({
    amount: "",
    password: "",
    re_password: "",
    weight: "",
    weightRange: "",
    showPassword: false,
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm({});

  const handleResetPassword = () => {
    const data = {
      password: password,
    };

    if (password == password_confirm) {
      axios
        .post(
          `http://127.0.0.1:8000/api/user/reset-password-client/${token}`,
          data
        )
        .then((response) => {
          setOpen(true);
          setTimeout(() => {
            setRedirect(true);
          }, 3000);
        })
        .catch((error) => {
          console.log(error.response.data.message);
        });
    } else {
      setError("Mật khẩu nhập lại không đúng !");
    }
  };
  const handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  if (redirect) {
    return <Redirect to="/Login" />;
  }
  const handleClickShowPassword = () => {
    setValues({
      ...values,
      showPassword: !values.showPassword,
    });
  };
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  return (
    <>
      <Header />
      <div className="noindex">
        <div id="layout-page-login" className="container">
          <div id="customer-login">
            <span className="header-contact header-page clearfix">
              <h4 id="title-login" style={{ marginTop: "20px" }}>
                Khôi phục mật khẩu
              </h4>
            </span>
            <div id="login" className="userbox">
              <div className="input-group">
                <FormControl sx={{ m: 1, width: "25ch" }} variant="outlined">
                  <InputLabel htmlFor="outlined-adornment">
                    Mật khẩu
                  </InputLabel>
                  <OutlinedInput
                    name="password"
                    id="outlined-adornment-password"
                    type={values.showPassword ? "text" : "password"}
                    {...register("password", { required: true })}
                    endAdornment={
                      <InputAdornment position="end">
                        <IconButton
                          aria-label="toggle password visibility"
                          onClick={handleClickShowPassword}
                          onMouseDown={handleMouseDownPassword}
                          edge="end"
                        >
                          {values.showPassword ? (
                            <VisibilityOff />
                          ) : (
                            <Visibility />
                          )}
                        </IconButton>
                      </InputAdornment>
                    }
                    label="Mật khẩu"
                  />
                  {errors?.password?.type === "required" && (
                    <p>Mật khẩu không được bỏ trống</p>
                  )}
                </FormControl>
              </div>
              {/* <div className="input-group"></div> */}

              <div className="action_bottom">
                <Button
                  variant="primary"
                  className="btnLogin"
                  type="submit"
                  onClick={handleResetPassword}
                >
                  Xác nhận
                </Button>
              </div>
              <Snackbar
                open={open}
                autoHideDuration={3000}
                onClose={handleClose}
              >
                <Alert onClose={handleClose} severity="success">
                  Chúc mừng bạn đã đổi mật khẩu thành công!
                </Alert>
              </Snackbar>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default ResetPassword;
