import React from "react";
import Footer from "../../Component/Footer/Footer";
import Header from "../../Component/Header/Header";
import { Button } from "react-bootstrap";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";
import "./Register.css";
import { useState } from "react";
import axios from "axios";
import { useSnackbar } from "notistack";
import CircularProgress from "@material-ui/core/CircularProgress";
import ImageUploading from "react-images-uploading";
import TextField from "@material-ui/core/TextField";
import Backdrop from "@material-ui/core/Backdrop";
import Box from "@material-ui/core/Box";
import {
  FormControl,
  IconButton,
  InputAdornment,
  InputLabel,
  OutlinedInput,
} from "@material-ui/core";
import { Visibility, VisibilityOff } from "@material-ui/icons";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/material.css";
function Register() {
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [Email, setEmail] = useState("");
  const [nameUser, setNameUser] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [sex, setSex] = useState(1);
  const [redirect, setRedirect] = useState(false);
  const [errorEmail, setErrorEmail] = useState([]);
  const [errorUsername, setErrorUsername] = useState([]);
  const [open, setOpen] = React.useState(false);
  const { enqueueSnackbar } = useSnackbar();
  const [images, setImages] = React.useState([]);
  const [imageUser, setImageUser] = useState();
  const [values, setValues] = React.useState({
    amount: "",
    password: "",
    re_password: "",
    weight: "",
    weightRange: "",
    showPassword: false,
  });
  const maxNumber = 69;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList);
    setImages(imageList);

    if (imageList[0] !== undefined) {
      setImageUser(imageList[0].data_url);
    } else {
      return;
    }
  };
  const onSubmit = (e) => {
    e.preventDefault();
    const data = {
      Email: Email,
      username: username,
      password: password,
      TenNguoidung: nameUser,
      SDT: phone,
      DiaChi: address,
      GioiTinh: sex,
      Anh: imageUser,
    };
    setOpen(true);

    axios
      .post("http://127.0.0.1:8000/api/Register", data)
      .then(() => {
        setTimeout(() => {
          enqueueSnackbar("Chúc mừng bạn đăng ký thành công !", {
            variant: "success",
            autoHideDuration: 3000,
          });
          setRedirect(true);
        }, 4000);
      })
      .catch((e) => {
        setOpen(false);
        console.log(e.response);
        setErrorEmail(e.response.data.Email);
        setErrorUsername(e.response.data.username);
        if (e.response.data.Email != undefined) {
          enqueueSnackbar(e.response.data.Email, {
            variant: "error",
            autoHideDuration: 3000,
            preventDuplicate: true,
          });
        }
        if (e.response.data.username != undefined) {
          enqueueSnackbar(e.response.data.username, {
            variant: "error",
            autoHideDuration: 3000,
            preventDuplicate: true,
          });
        }

        if (e.response.data.password != undefined) {
          enqueueSnackbar(e.response.data.password, {
            variant: "error",
            autoHideDuration: 3000,
            preventDuplicate: true,
          });
        }
      });
  };
  const handleClickShowPassword = () => {
    setValues({
      ...values,
      showPassword: !values.showPassword,
    });
  };

  const handleClickShowRePassword = () => {
    setValues({
      ...values,
      showPassword: !values.showPassword,
    });
  };
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };
  const handleClose = () => {
    setOpen(false);
  };
  if (redirect) {
    return <Redirect to="/Login" />;
  }
  return (
    <>
      <Header />
      <div className="noindex">
        <div id="layout-page-register" className="container">
          <span className="header-contact header-page clearfix">
            <h5 className="title-register">Tạo tài khoản</h5>
          </span>
          <div className="userbox col-lg-12" style={{ marginLeft: "27%" }}>
            <div className="input-group username">
              <TextField
                required
                id="outlined-basic-1"
                variant="outlined"
                name="name"
                type="text"
                label="Tên tài khoản"
              />
            </div>
          </div>
          <div className="userbox col-lg-12" style={{ marginLeft: "27%" }}>
            <div className="input-group name">
              <TextField
                required
                id="outlined-basic-2"
                variant="outlined"
                name="name"
                type="text"
                label="Họ tên"
              />
            </div>
          </div>

          <div className="userbox col-lg-12" style={{ marginLeft: "27%" }}>
            <div className="input-group email">
              <TextField
                required
                id="outlined-basic-3"
                variant="outlined"
                name="name"
                type="text"
                label="Email"
              />
            </div>
          </div>

          <div className="userbox col-lg-12" style={{ marginLeft: "27%" }}>
            <div className="input-group password">
              <FormControl sx={{ m: 1, width: "25ch" }} variant="outlined">
                <InputLabel htmlFor="outlined-adornment-password">
                  Mật khẩu
                </InputLabel>
                <OutlinedInput
                  id="outlined-adornment-password"
                  type={values.showPassword ? "text" : "password"}
                  value={values.password}
                  onChange={handleChange("password")}
                  endAdornment={
                    <InputAdornment position="end">
                      <IconButton
                        aria-label="toggle password visibility"
                        onClick={handleClickShowPassword}
                        onMouseDown={handleMouseDownPassword}
                        edge="end"
                      >
                        {values.showPassword ? (
                          <VisibilityOff />
                        ) : (
                          <Visibility />
                        )}
                      </IconButton>
                    </InputAdornment>
                  }
                  label="Mật khẩu"
                />
              </FormControl>

              <FormControl
                sx={{ m: 1, width: "25ch" }}
                variant="outlined"
                style={{ marginLeft: "10px" }}
              >
                <InputLabel htmlFor="outlined-adornment-password">
                  Nhập lại mật khẩu
                </InputLabel>
                <OutlinedInput
                  id="outlined-adornment-password"
                  type={values.showPassword ? "text" : "password"}
                  value={values.re_password}
                  onChange={handleChange("re_password")}
                  endAdornment={
                    <InputAdornment position="end">
                      <IconButton
                        aria-label="toggle password visibility"
                        onClick={handleClickShowRePassword}
                        onMouseDown={handleMouseDownPassword}
                        edge="end"
                      >
                        {values.showPassword ? (
                          <VisibilityOff />
                        ) : (
                          <Visibility />
                        )}
                      </IconButton>
                    </InputAdornment>
                  }
                  label="Nhập lại mật khẩu"
                />
              </FormControl>
            </div>
          </div>
          <div className="userbox col-lg-12" style={{ marginLeft: "27%" }}>
              <PhoneInput
                className="phone-number"
                country={"vn"}
              />
          </div>
          {/* <div className="userbox col-lg-6 d-flex"  style={{marginLeft:"13%"}}>
            <div className="input-group username">
              <TextField
                required
                id="outlined-basic-4"
                variant="outlined"
                name="name"
                type="text"
                label="Tên tài khoản"
              />
            </div>
            <div className="input-group name" style={{marginLeft:"20px"}}>
              <TextField
                required
                id="outlined-basic-5"
                variant="outlined"
                name="name"
                type="text"
                label="Họ và tên"
              />
            </div>
            <div className="input-group email" style={{marginLeft:"20px"}}>
              <TextField
                required
                id="outlined-basic-6"
                variant="outlined"
                name="name"
                type="text"
                label="Email"
              />
            </div>

          </div> */}
        </div>
      </div>
      <Footer />
    </>
  );
}

export default Register;
